import React, { useEffect, useRef, useState } from "react";
import io from "socket.io-client";

const socket = io.connect("http://localhost:5000");

function UserInterface() {
  const [localStream, setLocalStream] = useState(null);
  const [remoteStream, setRemoteStream] = useState(null);
  const [peerConnection, setPeerConnection] = useState(null);
  const [roomId, setRoomId] = useState("4lkuk");

  const localVideoRef = useRef();
  const remoteVideoRef = useRef();

  useEffect(() => {
    const configuration = { 'iceServers': [{ 'urls': 'stun:stun.l.google.com:19302' }] };
    const peerConn = new RTCPeerConnection(configuration);
    setPeerConnection(peerConn);

    // Get user media and set local stream
    navigator.mediaDevices.getUserMedia({ video: true, audio: true })
      .then((stream) => {
        setLocalStream(stream);
        localVideoRef.current.srcObject = stream;
        stream.getTracks().forEach(track => peerConn.addTrack(track, stream));
      })
      .catch((error) => {
        console.error("Error accessing media devices:", error);
      });

    // Listen for remote tracks
    peerConn.ontrack = (event) => {
      setRemoteStream(event.streams[0]);
      remoteVideoRef.current.srcObject = event.streams[0];
    };

    // Signaling logic
    socket.on("receiveOffer", async (offer) => {
      await peerConn.setRemoteDescription(offer);
      const answer = await peerConn.createAnswer();
      await peerConn.setLocalDescription(answer);
      socket.emit("sendAnswer", answer);
    });

    socket.on("receiveAnswer", async (answer) => {
      await peerConn.setRemoteDescription(answer);
    });

    socket.on("receiveIceCandidate", async (candidate) => {
      try {
        await peerConn.addIceCandidate(candidate);
      } catch (error) {
        console.error("Error adding ICE candidate:", error);
      }
    });

    // Clean up
    return () => {
      if (localStream) {
        localStream.getTracks().forEach(track => track.stop());
      }
      if (remoteStream) {
        remoteStream.getTracks().forEach(track => track.stop());
      }
      if (peerConnection) {
        peerConnection.close();
      }
    };
  }, []);

  const handleJoinRoom = () => {
    socket.emit("joinRoom", roomId);
  };

  return (
    <div>
      <h1>User Interface</h1>
      <video ref={localVideoRef} autoPlay muted style={{ width: "300px" }} />
      <video ref={remoteVideoRef} autoPlay style={{ width: "300px" }} />
      <button onClick={handleJoinRoom}>Join Room</button>
    </div>
  );
}

export default UserInterface;
