  //   });
  //   peer.on("signal", (data) => {
  //     socket.emit("answerCall", { signal: data, to: caller });
  //   });
  //   peer.on("stream", (stream) => {
  //     userVideo.current.srcObject = stream;
  //   });

  //   peer.signal(callerSignal);
  //   connectionRef.current = peer;
  // };
  const callUser = (mePart) => {
    const peer = new Peer({
      initiator: true,
      trickle: false,
      stream: stream,
      config: {
        iceServers: [
          { urls: "stun:stun.l.google.com:19302" },
          { urls: "stun:stun1.l.google.com:19302" },
          { urls: "stun:stun2.l.google.com:19302" }
        ]
      }
    });

    peer.on("icecandidate", (event) => {
      if (event.candidate) {
        socket.emit("sendIceCandidate", {
          candidate: event.candidate,
          to: mePart
        });
      }
    });

    peer.on("signal", (data) => {
      socket.emit("callUser", {
        userToCall: mePart,
        signalData: data,
        from: me,
        name: name
      })
    });

    peer.on("stream", (stream) => {
      userVideo.current.srcObject = stream
    });

    socket.on("callAccepted", (signal) => {
      setCallAccepted(true)
      peer.signal(signal)
    });

    connectionRef.current = peer
  };

  const answerCall = () => {
    setCallAccepted(true)
    const peer = new Peer({
      initiator: false,
      trickle: false,
      stream: stream,
      config: {
        iceServers: [
          { urls: "stun:stun.l.google.com:19302" },
          { urls: "stun:stun1.l.google.com:19302" },
          { urls: "stun:stun2.l.google.com:19302" }
        ]
      }
    });

    peer.on("icecandidate", (event) => {
      if (event.candidate) {
        socket.emit("sendIceCandidate", {
          candidate: event.candidate,
          to: caller
        });
      }
    });

    peer.on("signal", (data) => {
      socket.emit("answerCall", { signal: data, to: caller })
    });

    peer.on("stream", (stream) => {
      userVideo.current.srcObject = stream
    });

    peer.signal(callerSignal)
    connectionRef.current = peer
  };

  const leaveCall = () => {
    setCallEnded(true);
    connectionRef.current.destroy();
  };
  const [message, setMessage] = useState('');
  const printtag =()=>{
    setMessage("Click the Phone Icon");

  };

  return (
    <>
      <div className="Nav">
        <img src={companylogo} alt=""/>
        <h1 className="headd">Instant Meeting</h1>
      </div>

      <div className="container">
        <div className="video-container">
          <div className="video">
            {stream && <video playsInline muted ref={myVideo} autoPlay style={{ width: "300px" }} />}
          </div>
          <div className="video">
            {callAccepted && !callEnded ?
            <video playsInline ref={userVideo} autoPlay style={{ width: "600px"}} /> :
            null}
          </div>
        </div>
        <div className="myId">
          <CopyToClipboard text={sessionLink} style={{ marginBottom: "2rem" }}>
            <Button variant="contained" color="primary" startIcon={<AssignmentIcon fontSize="large" />}>
              Copy Invite Link
            </Button>
          </CopyToClipboard>

          {/* <TextField
            id="filled-basic"
            label="ID to call"
            variant="filled"
            value={idToCall}
           onChange={(e) => setIdToCall(e.target.value)}
          />  */}
          <div className="call-button">
            {callAccepted && !callEnded ? (
              <Button variant="contained" color="secondary" onClick={leaveCall}>
                End Call
              </Button>
            ) : (
              <IconButton color="primary" aria-label="call" onClick={() => callUser(idToCall)}>
                <PhoneIcon fontSize="large" />
              </IconButton>
            )}
           
           <p>{message}</p>
          </div>
        </div>
        <div className="call-accept">
          {receivingCall && !callAccepted ? (
            <div className="caller">
              <h1 >{name} is calling...</h1>
              <Button variant="contained" color="primary" onClick={answerCall}>
                Answer
              </Button>
            </div>
          ) : null}
        </div>
        
        <div className="btns">
        <div className="capture-button1">
            <Button variant="contained" color="primary" onClick={capturePhoto}>
              Capture Photo
            </Button>
          </div>
          
        
          <div className="capture-button1">
            <Button variant="contained" color="primary" onClick={()=> {Join();printtag()}}>
              Join Meeting 
            </Button>
          </div>

         
          <div className="capture-button1">
            <Button variant="contained" color="primary" onClick={createSession}>
              Create Session
            </Button>
          </div>

          <div className="capture-button1">
            <IconButton color="primary" aria-label="toggle mic" onClick={toggleMic}>
              {isMicMuted ? <MicOffIcon fontSize="large" /> : <MicIcon fontSize="large" />}
            </IconButton>
          </div>

          <div className="capture-button1">
          <IconButton color="primary" aria-label="toggle camera" onClick={toggleCamera}>
           {isVideoMuted ? <VideocamOffIcon fontSize="large" /> : <VideocamIcon fontSize="large" />}
          </IconButton>
          </div>
        </div>
        {capturedPhoto && (
          <div className="captured-photo">
            <h2>Captured Photo</h2>
          <div className="captured-photo1">

            <img src={capturedPhoto} alt="Captured" />
          </div>
          </div>
        )}
      </div>
    </>
  );
}

export default App;
